import React from 'react';
import ProductFace from './ProductFace.js';

class ProductPage extends React.Component {
  constructor(props) {
    super(props)
    this.state = { productNumber: 0 }
  }

  triggerFirstEnter = () => {
      this.setState({
        productNumber: this.state.productNumber + 1
      });
      alert(this.state.productNumber);
  }

  render(){
    return <div className="ProductPage">
      <ProductFace productNumber={this.state.productNumber}/>
      <button className="ProductButton" onClick={this.triggerFirstEnter}>Click to Enter</button>
    </div>;
  }

}

export default ProductPage;
